$(document).ready(function(){users=[],repos=[],$(".ghbtn").each(function(){var a=$(this).attr("repo");if(!a)return;repos.push(a);var b=a.split("/")[0];-1===users.indexOf(b)&&users.push(b)});for(var a=0;a<users.length;a++)$.ajax({type:"GET",url:"https://api.github.com/users/"+users[a]+"/repos?per_page=100",tryCount:0,retryLimit:3,async:!0,dataType:"json",success:function(a){for(var b=0;b<a.length;b++)if(-1!==repos.indexOf(a[b].full_name)){var c=a[b].full_name;$("div[repo='"+c+"']").children(".star").html('<i class="fa fa-star"></i> '+a[b].stargazers_count),$("div[repo='"+c+"']").children(".fork").html('<i class="fa fa-code-fork"></i> '+a[b].forks_count)}}})});
